import React, {useEffect} from 'react'

import {useDispatch, useSelector} from "react-redux" 
import Message from "../components/Message"
import {Link} from "react-router-dom"
import {Row, Col, ListGroup, Image, Form, Button, Card, NavItem} from "react-bootstrap"
import {addToCart} from "../actions/cartAction" 


// The match variable it available to this component, not needs to explicitly passing it
// To the screen, does match means the URL?
export default function CartScreen({match, location, history}) {
    const productId = match.params.id
    const qty = location.search? location.search.split("=")[1] : 1

    const dispatch = useDispatch()
    useEffect(()=>{
        if (productId) {dispatch(addToCart(productId, qty))}
    }, [dispatch, productId, qty])

    const cart = useSelector(state => state.cart)
    const {cartItems} = cart

    return (
        <Row>
            <Col md={8}>
                <h1> Shopping Cart</h1>
                {cartItems.length === 0? <Message> Your cart is empty <Link to="/"> Go Back</Link></Message>: (
                    <ListGroup variant="flush">
                        {cartItems.map(item => (
                            <ListGroup.Item key={item.product}>
                                <Row>
                                    <Col md={2}> <Image src={item.image} alt="item.name" fluid rounded /> </Col>
                                    <Col md={3}> <Link to={`/product/${item.product}`}> item.name </Link></Col>
