import React, { useEffect} from 'react'
 
import {LinkContainer} from "react-router-bootstrap"
import {Table, Button} from "react-bootstrap"
import {useDispatch, useSelector} from "react-redux"
import Message from "../components/Message"
import Loader from "../components/Loader"
import {listUsers, deleteUsers} from "../actions/userAction"



export default function UserListScreen({history}) {
    const dispatch = useDispatch()

    const userList = useSelector(state => state.userList)
    const {loading, error, users} = userList

    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} = userLogin

    const userDelete = useSelector(state => state.userDelete)
    const {success : successDelete} = userDelete
//  Not even allowed to stay on that page
    useEffect(()=>{if (userInfo && userInfo.isAdmin) {
        console.log("list users please")
        dispatch(listUsers())} else { console.log("going to login page"); history.push("/login")}}, [history, dispatch, successDelete, userInfo])

    const deleteHandler = (id) => {
        if (window.confirm("are you sure")) {dispatch(deleteUsers(id))}
    }

    return (
        <div>
        {loading? <Loader/> : error? <Message variant="danger"> {error} </Message> : (
            <Table striped bordered hover responsive className="table-sm">
                <thead>
                    <tr>
                        <th> ID </th>
                        <th> NAME </th>
                        <th> EMAIL </th>
                        <th> ADMIN </th>
                        <th> </th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user)=>{return (
                    <tr key={user._id}>
                        
                        <td> {user._id}</td>
                        <td> {user.name} </td>
                        <td> <a href={`mailto: ${user.email}`}> {user.email} </a></td>
                        <td> {
                        user.isAdmin? 
                        (<i className="fas fa-check" style={{color : "green"}}></i>): 
                        <i className = "fas fa-times" style={{color: "red"}}></i>
                        }
                        </td>
                        <td>
                        <LinkContainer to={`/admin/user/${user._id}/edit`}> 
                        <Button variant="light" className="btn-sm"> <i className="fas fa-edit"> </i></Button>
                        </LinkContainer>
                        <Button variant="danger" className="btn-sm" onClick={()=>{deleteHandler(user._id)}}>
                        <i className="fas fa-trash"></i>
                        </Button>
                        </td>
                    </tr>
                    )})}
                </tbody>
            </Table>
            ) }
        </div>
    )
}
