import React, { useEffect} from 'react'

import {LinkContainer} from "react-router-bootstrap"
import {Table, Button} from "react-bootstrap"
import {useDispatch, useSelector} from "react-redux"
import Message from "../components/Message"
import Loader from "../components/Loader"
import {listAllOrders} from "../actions/orderAction"




export default function OrderListScreen({history}) {
    console.log("Order List Screen")

    const dispatch = useDispatch()

    //    introduce global variables into this screen
    const orderListAll = useSelector(state => state.orderListAll)
    const {loading, error, orders} = orderListAll

    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} = userLogin



    // const userDelete = useSelector(state => state.userDelete)
    // const {success : successDelete} = userDelete


// //  Not even allowed to stay on that page
//     useEffect(()=>{if (userInfo && userInfo.isAdmin) {
//         console.log("list orders please")
//         dispatch(listAllOrders())} else { console.log("going to login page"); history.push("/login")}}, 
//       [history, dispatch, successDelete, orderInfo])

    const deleteHandler = (id) => {
        if (window.confirm("are you sure")) {
            // dispatch(deleteOrders(id))
        }
    }

    return (
        <div>
        {loading? <Loader/> : error? <Message variant="danger"> {error} </Message> : (
            <Table striped bordered hover responsive className="table-sm">
                <thead>
                    <tr>
                        <th> ID </th>
                        <th> Total Price </th>
                        <th>  </th>
                        <th>  </th>
                        <th>  </th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map((order)=>{return (
                    <tr key={order._id}>

                        <td> {order._id}</td>
                        <td> {order.totalPrice} </td>
                        <td>  </td>
                        <td> 

                        (<i className="fas fa-check" style={{color : "green"}}></i>)
                        <i className = "fas fa-times" style={{color: "red"}}></i>}
                        </td>
                        <td>
                        <LinkContainer to={`/admin/order/${order._id}/edit`}> 
                        <Button variant="light" className="btn-sm"> <i className="fas fa-edit"> </i></Button>
                        </LinkContainer>
                        <Button variant="danger" className="btn-sm" onClick={()=>{deleteHandler(order._id)}}>
                        <i className="fas fa-trash"></i>
                        </Button>
                        </td>
                    </tr>
                    )})}
                </tbody>
            </Table>
            ) }
        </div>
    )
}
