import {
        ORDER_CREATE_REQUEST, 
        ORDER_CREATE_SUCCESS, 
        ORDER_CREATE_FAIL,
        ORDER_DETAILS_REQUEST, 
        ORDER_DETAILS_SUCCESS, 
        ORDER_DETAILS_FAIL,
        ORDER_PAY_REQUEST, 
        ORDER_PAY_SUCCESS, 
        ORDER_PAY_FAIL,
        ORDER_PAY_RESET,
        ORDER_LIST_MY_REQUEST,
        ORDER_LIST_MY_SUCCESS,
        ORDER_LIST_MY_FAIL,


    } from "../constants/orderConstants"

export const orderCreateReducer = (state = {} , action) => {
    switch(action.type) {
        case ORDER_CREATE_REQUEST    : {
            console.log("create request")
            return {loading: true}
        }
        case ORDER_CREATE_SUCCESS : {
            return { order: action.payload, loading: false , success: true}
        }
        case ORDER_CREATE_FAIL : {
            return {loading: false, error: action.payload}
        }       
        default: return state
    }
}

//* What is the best practise to design the initial point ??? */
export const orderDetailsReducer = (state = {orderItems: [], shippingAddress: {}, loading: true} , action) => {
    switch(action.type) {
        case ORDER_DETAILS_REQUEST    : {
            console.log("DETAILS request")
            return {...state, loading: true}
        }
        case ORDER_DETAILS_SUCCESS : {
            return { order: action.payload, loading: false }
        }
        case ORDER_DETAILS_FAIL : {
            return {loading: false, error: action.payload}
        }       
        default: return state
    }
}

export const orderPayReducer = (state = {} , action) => {
    switch(action.type) {
        case ORDER_PAY_REQUEST    : {
            console.log("PAY request")
            return { loading: true}
        }
        case ORDER_PAY_SUCCESS : {
            return { loading: false,
                      success: true
            }
        }
        case ORDER_PAY_FAIL : {
            return {loading: false, error: action.payload}
        }       
        case ORDER_PAY_RESET : {
            return { }
        }  
        default: return state
    }
}

export const orderListMyReducer = (state = {} , action) => {
    switch(action.type) {
        case ORDER_LIST_MY_REQUEST    : {
            console.log("LIST_MY request")
            return { loading: true}
        }
        case ORDER_LIST_MY_SUCCESS : {
            return { loading: false,
                     orders: true
            }
        }
        case ORDER_LIST_MY_FAIL : {
            return {loading: false, error: action.load}
        }       

        default: return state
    }
}
