import {USER_LOGIN_REQEUST, 
        USER_LOGIN_SUCCESS, 
        USER_LOGIN_FAIL, 
        USER_LOGOUT,
        USER_REGISTER_REQEUST, 
        USER_REGISTER_SUCCESS, 
        USER_REGISTER_FAIL,
        USER_LIST_REQEUST, 
        USER_LIST_SUCCESS, 
        USER_LIST_FAIL,
        USER_LIST_RESET,
        USER_DETAILS_REQEUST, 
        USER_DETAILS_SUCCESS, 
        USER_DETAILS_FAIL,
        USER_DETAILS_RESET,
        USER_UPDATE_PROFILE_REQEUST, 
        USER_UPDATE_PROFILE_SUCCESS, 
        USER_UPDATE_PROFILE_FAIL,
        USER_UPDATE_PROFILE_RESET
    } from "../constants/userConstant" 

export const userLoginReducer = (state= {userInfo}, action) => {
    switch(action.type) {
        case USER_LOGIN_REQEUST: {return {loading: true}}
        case USER_LOGIN_SUCCESS: {return {loading: false, userInfo: action.payload}}
        case USER_LOGIN_FAIL   : {return {loading: false, error: action.payload} }
        case USER_LOGOUT   : {return { } }
        default: {return state}
    }
}

export const userRegisterReducer = (state= {}, action) => {
    switch(action.type) {
        case USER_REGISTER_REQEUST: {return {loading: true}}
        case USER_REGISTER_SUCCESS: {return {loading: false, userInfo: action.payload}}
        case USER_REGISTER_FAIL   : {return {loading: false, error: action.payload} }

        default: {return state}
    }
}

export const userDetailsReducer = (state= { user: {} }, action) => {
    switch(action.type) {
        case USER_DETAILS_REQEUST: {return {...state, loading: true}}
        case USER_DETAILS_SUCCESS: {return {loading: false, user: action.payload}}
        case USER_DETAILS_FAIL   : {return {loading: false, error: action.payload} }  
        case USER_DETAILS_RESET   : {return { user: {} } }  
        default: {return state}
    }
}

export const userUpdateProfileReducer = (state= { }, action) => {
    switch(action.type) {
        case USER_UPDATE_PROFILE_REQEUST: {return {loading: true}}
        case USER_UPDATE_PROFILE_SUCCESS: {return {loading: false, success: true, userInfo: action.payload}}
        case USER_UPDATE_PROFILE_FAIL   : {return {loading: false, error: action.payload } } 
        case USER_UPDATE_PROFILE_RESET   : {return {} } 
        default: {return state}
    }
}

export const userListReducer = (state= { users: []}, action) => {
    switch(action.type) {
        case USER_LIST_REQEUST: {return {loading: true}}
        case USER_LIST_SUCCESS: {return {loading: false, users: action.payload}}
        case USER_LIST_FAIL   : {return {loading: false, error: action.payload } } 
        case USER_LIST_RESET   : {return { users: [] } } 
        default: {return state}
    }
}
