import React, {useState, useEffect} from 'react'
import {Link} from "react-router-dom"
import {Form, Button} from "react-bootstrap"
import {useDispatch, useSelector} from "react-redux"
import Message from "../components/Message"
import Loader from "../components/Loader"
import {listProductDetails} from "../actions/productAction"



export default function ProductEditScreen( {match, history} ) {
//  This is going to focus on using local state
    const productId = match.params.id

    const [name, setName] = useState("")
    const [price, setPrice] = useState(0)
    const [image, setIsAdmin] = useState(false)

    // This is the main global state for this component: productDetails
    const dispatch = useDispatch()
    const productDetails = useSelector(state => state.productDetails)
    const {loading, error, product} = productDetails

    const productUpdate = useSelector(state => state.productUpdate)
    const {loading: loadingUpdate, error: errorUpdate, success: successUpdate} = productUpdate
    console.log("product", product)


    useEffect(()=>{
        if (successUpdate) { history.push("/admin/Userlist")} else {
            if (! product.name || product._id !== productId ) 
            {dispatch(listProductDetails(productId)); console.log("useEffect", product)} else {
            setName(product.name)
            setEmail(product.email)
            setIsAdmin(product.isAdmin)}}
    }, [product, dispatch, history, productId, successUpdate])


    const submitHandler = (e) => {
        e.preventDefault()       
        dispatch(updateProduct({_id: productId, name, email, isAdmin}))
    }

//  all stuff inside is the children
    return (

        <div>

            <Link to= "/admin/userlist" className="btn btn-light my-3"> Go Back </Link>
            <FormContainer>
                 <h1> Edit Product </h1>
                {loadingUpdate && <Loader> </Loader>}
                {errorUpdate && <Message variant="danger"> {error.update} </Message>}
                {loading? <Loader> </Loader> : error?( <Message variant="danger"> {error} </Message>) :  (

                <Form onSubmit = {submitHandler} >

                <Form.Group controlId="name">
                    <Form.Label> Name </Form.Label>
                    <Form.Control type="name" placeholder="Enter name" value={name} onChange={(e)=>{setName(e.target.value)}}/> 
                </Form.Group> 

                <Form.Group controlId="email">
                    <Form.Label> Email Address </Form.Label>
                    <Form.Control type="email" placeholder="Enter email" value={email} onChange={(e)=>{setEmail(e.target.value)}}/> 
                </Form.Group> 

                <Form.Group controlId="isAdmin">
                    <Form.Check type="checkbox"  label="isAdmin" checked={isAdmin} onChange={(e)=>{setIsAdmin(e.target.checked)}}/> 
                </Form.Group>  

                <Button type="submit" variant="primary"> Update </Button>
                </Form>
            )}



             </FormContainer>
        </div>

    )
}
