import {PRODUCT_LIST_REQUEST, PRODUCT_LIST_SUCCESS, PRODUCT_LIST_FAIL,
        PRODUCT_DETAILS_REQUEST, PRODUCT_DETAILS_SUCCESS, PRODUCT_DETAILS_FAIL,
        PRODUCT_DELETE_REQUEST, PRODUCT_DELETE_SUCCESS, PRODUCT_DELETE_FAIL
} from "../constants/productConstants"


export const productListReducer = (state= {products: []}, action) => {
    switch(action.type) {
        case PRODUCT_LIST_REQUEST: {return {loading: true,  products: []}}
        case PRODUCT_LIST_SUCCESS: {return {loading: false, products: action.payload}}
        case PRODUCT_LIST_FAIL   : {return {loading: false, error: action.payload} }
        default: {return state}
    }
}

export const productDetailsReducer = (state= {}, action) => {
    switch(action.type) {
        case PRODUCT_DETAILS_REQUEST: {return {loading: true,  ...state}}
        case PRODUCT_DETAILS_SUCCESS: {return {loading: false, product: action.payload}}
        case PRODUCT_DETAILS_FAIL   : {return {loading: false, error: action.payload} }
        default: {return state}
    }
}

export const productDeleteReducer = (state= {product: {reviews: []}}, action) => {
    switch(action.type) {
        case PRODUCT_DELETE_REQUEST: {return {loading: true,  ...state}}
        case PRODUCT_DELETE_SUCCESS: {return {loading: false, product: action.payload}}
        case PRODUCT_DELETE_FAIL   : {return {loading: false, error: action.payload} }
        default: {return state}
    }
}


// the return function will set what that state is going to be looklike
// Reducer state: {product: []}
// Reducer state: {product: action.payload, loading: true}
