import React, {useEffect} from 'react'

import {useDispatch, useSelector} from "react-redux" 
import Message from "../components/Message"
import {Link} from "react-router-dom"
import {Row, Col, ListGroup, Image, Form, Button, Card} from "react-bootstrap"
import {addToCart, removeFromCart} from "../actions/cartAction" 



// The match variable it available to this component, not needs to explicitly passing it
// To the screen, does match means the URL?
export default function CartScreen({match, location, history}) {
    const productId = match.params.id
    const qty = location.search? location.search.split("=")[1] : 1

    const cart = useSelector(state => state.cart)
    const {cartItems} = cart

    const dispatch = useDispatch()
    useEffect(()=>{
        if (productId) {dispatch(addToCart(productId, qty))}
    }, [dispatch, productId, qty])

    const removeFromCartHandler = (id) => {
        console.log("trying to remove")
        console.log(id)
        dispatch(removeFromCart(id))
    }

    const checkoutHandler = () => {console.log("checkout")
        history.push("/login?redirect=shipping")
    }

//  login is true? redirect
//  login is false, then login
 

    return (
        <Row>
            <Col md={8}>
                <h1> Shopping Cart</h1>
                {cartItems.length === 0? <Message> Your cart is empty <Link to="/"> Go Back</Link></Message>: (
                    <ListGroup variant="flush">
                        {cartItems.map(item => (
                            <ListGroup.Item key={item.product}>
                                <Row>
                                    <Col md={2}> <Image src={item.image} alt="item.name" fluid rounded /> </Col>
                                    <Col md={3}> <Link to={`/product/${item.product}`}> {item.name} </Link></Col>
                                    <Col md={2}> {item.price} </Col>
                                    <Col md={2}> 
                                        <Form.Control as="select" value={item.qty} onChange={(e)=>{dispatch(addToCart(item.product, Number(e.target.value)))}}> 
                                        {
                                            [...Array(item.countInStock).keys()].map(
                                              (num) => (<option key={num+1} value={num+1}> 
                                                {num+1}
                                            </option>))}
                                        </Form.Control> 
                                    </Col>
                                    <Col md={2} >
                                        <Button type="button" variant="light" onClick={()=>removeFromCartHandler(item.product)}> <i className = "fas fa-trash"></i> </Button>
                                        
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                        ))}
                    </ListGroup>
                )}
            </Col>

            <Col md={4}> 
                <Card>
                    <ListGroup variant="flush">
                        <ListGroup.Item>
                            <h2>Subtotal ({cartItems.reduce((acc, item) => acc + Number(item.qty), 0)}) items </h2>    
                            ${cartItems.reduce((acc, item)=> acc + item.qty * item.price, 0).toFixed(2)}                
                        </ListGroup.Item>
                        <ListGroup.Item>
                            <Button type="button" className="btn-block" disabled={cartItems.length === 0} onClick={checkoutHandler}> Proceed to Checkout </Button>
                        </ListGroup.Item>
                    </ListGroup>
                </Card>
            </Col>
            
        </Row>
    )
}
