import React, { useEffect} from 'react'

import {LinkContainer} from "react-router-bootstrap"
import {Table, Button, Row, Col} from "react-bootstrap"
import {useDispatch, useSelector} from "react-redux"
import Message from "../components/Message"
import Loader from "../components/Loader"
import {listProducts, deleteProduct, createProduct} from "../actions/productAction"
import {PRODUCT_CREATE_RESET} from "../constants/productConstants"
import Paginate from "../components/Paginate"


export default function ProductListScreen({history, match}) {
    const pageNumber = match.params.pageNumber || 1


    const dispatch = useDispatch()

    const productList = useSelector(state => state.productList)
    const {loading, error, products, pages, page} = productList

    const productDelete = useSelector(state => state.productDelete)
    const {loading: loadingDelete, error: errorDelete, success: successDelete} = productDelete

    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} = userLogin

    const productCreate = useSelector(state => state.productCreate)
    const {loading: loadingCreate, error: errorCreate, success: successCreate, product: createdProduct} = productCreate

//  Not even allowed to stay on that page
    useEffect(()=>{
        dispatch({type: PRODUCT_CREATE_RESET})
        console.log("userInfo", userInfo)
        if (!userInfo || !userInfo.isAdmin) {history.push("/login")}
        console.log("in the useEffect", "successCreate", successCreate, "createproduct", createdProduct,)
        if(successCreate) {history.push(`/admin/product/${createdProduct._id}/edit`)} else {dispatch(listProducts("", pageNumber))}
    }, [history, dispatch, userInfo, successDelete, successCreate, createdProduct, pageNumber])

    const deleteHandler = (id) => {
        if (window.confirm("are you sure")) {dispatch(deleteProduct(id))}
    }

    const createProductHandler = () => {
        dispatch(createProduct())
    }

    return (


        <div>
            <Row className="align-items-center">
                <Col>
                    <h1> Products</h1>
                </Col>
                <Col className="text-right">
                    <Button className="m-3" onClick={createProductHandler}>
                        <i className="fas fa-plus"></i> Create Product
                    </Button>
                </Col>
            </Row>
        {loadingDelete && <Loader></Loader>}
        {errorDelete && <Message variant="danger"> {errorDelete} </Message>}
        {loadingCreate && <Loader></Loader>}
        {errorCreate && <Message variant="danger"> {errorCreate} </Message>}
        {loading? <Loader/> : error? <Message variant="danger"> {error} </Message> : (
            <div>
                <Table striped bordered hover responsive className="table-sm">
                    <thead>
                        <tr>
                            <th> ID </th>
                            <th> NAME </th>
                            <th> Price </th>
                            <th> Category </th>
                            <th> Brand </th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map((product)=>{return (
                        <tr key={product._id}>

                            <td> {product._id}</td>
                            <td> {product.name} </td>
                            <td> $ {product.price}</td>
                            <td> {product.category} </td>
                            <td> {product.brand} </td>
                            <td>
                                <LinkContainer to={`/admin/product/${product._id}/edit`}> 
                                    <Button variant="light" className="btn-sm"> <i className="fas fa-edit"> </i></Button>
                                </LinkContainer>
                                    <Button variant="danger" className="btn-sm" onClick={()=>{deleteHandler(product._id)}}>
                                <i className="fas fa-trash"></i>
                                </Button>
                            </td>
                        </tr>
                        )})}
                    </tbody>
                </Table>
                <Paginate pages={pages} page={page} isAdmin={true}>

                </Paginate>
            </div>
            ) }
        </div>
    )
}
