import React, {useState, useEffect} from 'react'
import {Link} from "react-router-dom"
import {Row, Col, Image, ListGroup, Card, Button, Form} from "react-bootstrap"
import Rating from "../components/Rating"
import {useDispatch, useSelector} from "react-redux"
// import products from "../products"
// import axios from "axios"
import {listProductDetails} from "../actions/productAction"
import Message from  "../components/Message"
import Loader from  "../components/Loader"

// props.match is the url in the react system
const ProductScreen = ({history, match}) => {
    // const [product, setProduct] = useState({})
//  There are lots of limitation to useEffect, 1st argument is function, cannot use Async there, have to define one, then invoke it
//  for axios, response.data is the one we want 
    const dispatch = useDispatch()
    const productDetails = useSelector(state => state.productDetails)
    // state ---- reducer ---- loading, error, product
    const {loading, error, product} = productDetails // destruction a good way to extra data 

    useEffect( ()=> {
        // const fetchProduct = async () => {const {data} = await axios.get(`/api/products/${match.params.id}`); setProduct(data)}
        // fetchProduct();
        dispatch(listProductDetails(match.params.id))
    }, [match, dispatch])

    const [qty, setQty] = useState(0)

    // const product = products.find(product => product._id === match.params.id)

    const addToCartHandler = () => {
        console.log("start Redirecting to cart page")
        history.push(`/cart/${match.params.id}?qty=${qty}`)
    }


    return (

        <div>
            <Link classNmae = "btn btn-light my-3" to="/"> Go Back </Link>

            {loading? <Loader/> : error? <Message variant="danger"> {error} </Message> : (

                <Row>
                <Col md={6}>
                    <Image src={product.image} alt={product.name} fluid/>
                </Col>

                <Col md={3}>
                    <ListGroup variant = "flush"> 
                        <ListGroup.Item>
                            <h3> {product.name} </h3>
                        </ListGroup.Item>
                        <ListGroup.Item>
                            <Rating value={product.rating} text={`${product.numReviews} reviews`}/>
                        </ListGroup.Item>
                        <ListGroup.Item>
                            Price: ${product.price}
                        </ListGroup.Item>
                        <ListGroup.Item>
                            Description: ${product.description}
                        </ListGroup.Item>
                    </ListGroup>
                </Col>

                <Col md={3}>
                    <Card>
                        <ListGroup variant="flush">
                            <ListGroup.Item> 
                                <Row>
                                    <Col>
                                        Price:
                                    </Col>
                                    <Col>
                                        <strong> {product.price} </strong>
                                    </Col>
                                </Row>
                            </ListGroup.Item>
                            <ListGroup.Item> 
                                <Row>
                                    <Col>
                                        Status:
                                    </Col>
                                    <Col>
                                        {product.countInStock > 0? "In Stock" : "Out of Stock"}
                                    </Col>
                                </Row>
                            </ListGroup.Item>

                            {product.countInStock > 0 && (
                                <ListGroup.Item>
                                    <Row>
                                        <Col>Qty</Col>
                                        <Col> 
                                            <Form.Control as="select" value={qty} onChange={(e)=>{consetQty(e.target.value)}}> 
                                                {[...Array(product.countInStock).keys()].map(
                                                    (num) => (<option key={num+1} value={num+1}> 
                                                            {num+1}
                                                        </option>))}
                                            </Form.Control>
                                        </Col>
                                    </Row>
                                </ListGroup.Item>
                            )}

                            <ListGroup.Item>                           
                                <Button 
                                className= "btn-block" 
                                type="button" 
                                disabled={product.countInStock === 0}
                                onclick = {addToCartHandler}
                                >
                                    Add to cart
                                </Button> 
                            </ListGroup.Item>
                        </ListGroup>
                    </Card>
                </Col>
            </Row>

            )}

        </div>
    )
}

export default ProductScreen


// The match object in router case
// import is more like file path
// to is url
// In react, when you talk about src, it means something in the public folder
